<script setup>
import Tasks from '../components/Tasks.vue'
import { onMounted, ref, reactive } from 'vue';

const time = ref('')
const task = ref('')
const date = ref('')
const duration = ref('')
const Priority = reactive([
   { id: 1, level: 'High' },
   { id: 2, level: 'Medium' },
   { id: 3, level: 'Low' }
])
const isClicked = ref(false)
const selectedPriority = ref(null)
const display = ref(false)

const hour = reactive([
   { id: 1, timer: '01 Hour' },
   { id: 2, timer: '02 Hours' },
   { id: 3, timer: '03 Hours' },
   { id: 4, timer: '04 Hours' },
   { id: 5, timer: '05 Hours' },
   { id: 6, timer: '06 Hours' },
   { id: 7, timer: '07 Hours' },
   { id: 8, timer: '08 Hours' },
   { id: 9, timer: '09 Hours' },
   { id: 10, timer: '10 Hours' },
   { id: 11, timer: '11 Hours' },
   { id: 12, timer: '12 Hours' },
   { id: 13, timer: '13 Hours' },
   { id: 14, timer: '14 Hours' },
   { id: 15, timer: '15 Hours' },
   { id: 16, timer: '16 Hours' },
   { id: 17, timer: '17 Hours' },
   { id: 18, timer: '18 Hours' },
   { id: 19, timer: '19 Hours' },
   { id: 20, timer: '20 Hours' },
   { id: 21, timer: '21 Hours' },
   { id: 22, timer: '22 Hours' },
   { id: 23, timer: '23 Hours' },
   { id: 24, timer: '24 Hours' },
])

const userResponses = ref([])
// const emit = defineEmits(['open'])

// METHODS
const priorityCheck = (clickedPriority) => {
   isClicked.value = true
   selectedPriority.value = Priority.filter(item => item.id === clickedPriority)
}

const openPriorityCheck = () => {
   isClicked.value = false
}

const timeUpdate = () => {
   setInterval(() => {
      const date = Date()
      if (date === '') return;
      let hour = Number((new Date()).getHours()) + ':'
      if ((hour).toString().length == 1) hour += "0";
      let min = new Date().getMinutes();
      time.value = ` ${hour}${min}`
   }, 1000);
}
const classObject = (itemId) => {
   if (itemId === 1) {
      return ['text-[#FF4242]', 'bg-[#FF424214]', 'p-2']
   } else if (itemId === 2) {
      return ['text-[#FBB01E]', 'bg-[#FBB01E14]', 'p-2']
   } else {
      return ['text-[#56EC77]', 'bg-[#56EC7714]', 'p-2']
   }
}

function showToastAlert() {
   Toastify({
      text: "Task Updated Successfully!",
      duration: 3000,
      gravity: "top", 
      position: "center", 
      className: 'rounded-lg',
      style: {
         background: 'rgba(240, 253, 244, 50)',
         color: 'rgba(22, 101, 52, 1)',
         border: 'solid 1px rgba(134, 239, 172, 1)', 
      }
   }).showToast();
}

const addTask = () => {
   userResponses.value.push({
      task: task.value,
      date: date.value,
      duration: duration.value,
      priority: selectedPriority.value[0].level
   })
   showToastAlert()
   task.value = "";
   date.value = "";
   duration.value = "";
   // selectedPriority.value = "";;
}

const toggleDisplay = () => {
    display.value = !display.value
}

// METHODS END


onMounted(() => {
   timeUpdate()
})

</script>
